        public string RunOne()
        {
            int floor = 0;

            int count = 1;

            foreach (char c in input)
            {
                if (c == '(') floor++;
                else floor--;
            }
            return floor.ToString();
        }

        public string RunTwo()
        {
            int floor = 0;

            int count = 1;

            foreach (char c in input)
            {
                if (c == '(') floor++;
                else floor--;

                if (floor == -1)
                {
                    return count.ToString();
                }
                count++;
            }

            return null;
        }
